const adviceId=document.querySelector("#A-ID"),advice=document.querySelector("#advice-text"),btn=document.querySelector(".dice-container");let message;async function getAdvice(){var e="https://api.adviceslip.com/advice/"+Math.floor(200*Math.random());try{message=await fetch(e).then(e=>e.json()),adviceId.textContent=message.slip.id,50<message.slip.advice.length?(advice.classList.add("long"),advice.classList.remove("advice-text")):(advice.classList.remove("long"),advice.classList.add("advice-text")),advice.textContent=`"${message.slip.advice}"`}catch(e){}}btn.addEventListener("click",()=>{getAdvice()}),getAdvice();